#!/usr/bin/env python# -*- coding: utf-8 -*-#小兵等级信息class _BatmanInfo():    def __init__(self,dao = 43101,qiang = 43201,chui = 43301, gong = 43401,ceshi = 43501,nvbing = 43601):        self.dao = dao        self.qiang = qiang        self.chui = chui        self.gong = gong        self.ceshi = ceshi        self.nvbing = nvbing    def getBatmanDatWithNumber(self,pType):        if pType == 1:            return self.dao        if pType == 2:            return self.qiang        elif pType == 3:            return self.chui        elif pType == 4:            return self.gong        elif pType == 5:            return self.ceshi        elif pType == 6:            return self.nvbing    def setBatmanDatWithMysqlData(self,datas):        #(u'usertest154', 43101L, 43201L, 43301L, 43401L, 43501L, 43601L)        self.dao = int(datas[1])        self.qiang = int(datas[2])        self.chui = int(datas[3])        self.gong = int(datas[4])        self.ceshi = int(datas[5])        self.nvbing = int(datas[6])    def getBatmanInfoForList(self):#     1    刀#     2    枪#     3    锤#     4    弓#     5    策#     6    女        return [self.dao,self.qiang,self.chui,self.gong,self.ceshi,self.nvbing]    def getBatmanInfoForProtobuff(self,tmp):        #tmp = Cmds_pb2.BatmanInfo()        tmp.batmanCId.append(self.dao)        tmp.batmanCId.append(self.qiang)        tmp.batmanCId.append(self.chui)        tmp.batmanCId.append(self.gong)        tmp.batmanCId.append(self.ceshi)        tmp.batmanCId.append(self.nvbing)        return tmp    def getBatmanInfoForProtobuffString(self):        return self.getBatmanInfoForProtobuff().SerializePartialToString()