#!/usr/bin/env python# -*- coding: utf-8 -*-#//protobuff英雄class _Hero():    def __init__(self,heroCID = 0,heroGarrisonID = [],heroNetID = 0,heroLevel = 1,heroStar = 0,heroUserID = 0,isOutHero = 0,heroLastType = 0,heroLastTime = 0,fighting = 0,skillID = 0):        # {'heroGarrisonID':heroGarrisonID,'heroNetID':heroNetID,'heroCID':heroCID,'heroLevel':heroLevel,'heroStar':heroStar,'heroReborn':heroReborn,'heroUserID':heroUserID,'herodiff':herodiff,'heroLastType':heroLastType}        self.heroNetID = int(heroNetID)            self.heroGarrisonID = heroGarrisonID        self.heroCID = int(heroCID)        self.heroLevel = int(heroLevel)        self.heroStar = int(heroStar)        self.heroUserID = str(heroUserID)        self.isOutHero = int(isOutHero)        self.fighting = int(fighting)        self.skillNetID = int(skillID)        self.heroLastType = int(heroLastType)        self.heroLastTime = int(heroLastTime)    def getHeroDataForDic(self):        return {'heroGarrisonID':self.heroGarrisonID,'heroNetID':self.heroNetID,'heroCID':self.heroCID,'heroLevel':self.heroLevel,'heroStar':self.heroStar,'heroUserID':self.heroUserID,'herodiff':self.herodiff,'heroLastType':self.heroLastType,'heroLastTime':self.heroLastTime}    def getHeroDataForProtoBuff(self,tmp):        #tmp = Cmds_pb2.Hero()        for i in self.heroGarrisonID:            tmp.heroGarrisonID.append(i)        tmp.heroNetID = int(self.heroNetID)        tmp.heroCID = int(self.heroCID)        tmp.heroLevel = int(self.heroLevel)        tmp.heroStar = int(self.heroStar)        tmp.heroUserID = str(self.heroUserID)        tmp.herodiff = int(self.herodiff)        tmp.heroLastType = int(self.heroLastType)        tmp.heroLastTime = int(self.heroLastTime)        tmp.fighting = int(self.fighting)        return tmp    def getHeroDataForProtobuffString(self):        return self.getHeroDataForProtoBuff().SerializePartialToString()    def setHeroDataWithMysqlHeroData(self,mysqlheroDat):        pass    #技能class _HeroSkill():    def __init__(self,skillNetID = 0,skillCID = 0,skillType = 0,skillLevel = 0,skillHeroNetID = 0):        #{'skillNetID':skillNetID,'skillCID':skillCID,'skillType':skillType,'skillLevel':skillLevel,'skillHeroNetID':skillHeroNetID}        self.skillNetID = skillNetID        self.skillCID = skillCID                #技能属于英雄独有，所以不会重复        self.skillType = skillType        self.skillLevel = skillLevel        self.skillHeroNetID = skillHeroNetID    def getHeroSkillForDic(self):        return {'skillNetID':self.skillNetID,'skillCID':self.skillCID,'skillType':self.skillType,'skillLevel':self.skillLevel,'skillHeroNetID':self.skillHeroNetID}    def getHeroSkillForProtobuff(self,tmp):        #tmp = Cmds_pb2.HeroSkill()        tmp.skillNetID = int(self.skillNetID)        tmp.skillCID = int(self.skillCID)        tmp.skillType = int(self.skillType)        tmp.skillLevel = int(self.skillLevel)        tmp.skillHeroNetID = int(self.skillHeroNetID)        return tmp    def getHeroSkillForProtobuffString(self):        return self.getHeroSkillForProtobuff().SerializePartialToString()        