#!/usr/bin/env python# -*- coding: utf-8 -*-#用户基础数据class _UserBase():    def __init__(self,level = 1,vipLevel = 0,vipScore = 0,name = '',buyWarehouseSpace = 0,buyHeroSpace = 0,buyJunling = 0,energyItemUseTimes = 0,exp = 0):        #self.userBase = {'level':level,'vipLevel':vipLevel,'vipScore':vipScore,'name':name,'buyWarehouseSpace':buyWarehouseSpace,'buyHeroSpace':buyHeroSpace,'buyJunling':buyJunling,'energyItemUseTimes':energyItemUseTimes}        self.level = level        self.exp = 0        self.vipLevel = vipLevel        self.vipScore = vipScore        self.name = name        self.buyWarehouseSpace = buyWarehouseSpace        self.buyHeroSpace = buyHeroSpace        self.buyJunling = buyJunling        self.energyItemUseTimes = energyItemUseTimes    def getUserBaseDataForDic(self):        return {'level':self.level,'vipLevel':self.vipLevel,'vipScore':self.vipScore,'name':self.name,'buyWarehouseSpace':self.buyWarehouseSpace,'buyHeroSpace':self.buyHeroSpace,'buyJunling':self.buyJunling,'energyItemUseTimes':self.energyItemUseTimes}    def getUserBaseDataForProtobuff(self,tmp):        #tmp = Cmds_pb2.User()        tmp.level = self.level        tmp.vipLevel = self.vipLevel        tmp.vipScore = self.vipScore        tmp.name = self.name        tmp.buyWarehouseSpace = self.buyWarehouseSpace        tmp.buyHeroSpace = self.buyHeroSpace        tmp.buyJunling = self.buyJunling        tmp.energyItemUseTimes = self.energyItemUseTimes        return tmp    def getUserBaseDataForProtobuffString(self):        return self.getUserBaseDataForProtobuff().SerializePartialToString()