#!/usr/bin/env python# -*- coding: utf-8 -*-import time#用户其他数据class _UserOtherData():    def __init__(self,newHandStep = 0,loginRewardStats = 0,leveupRewardStats = 0,lastGetEmailTime = 0,bugTimes = 0,addEnergyTime = int(time.time()),serverTime = int(time.time())):        #self.userOtherData = {'newHandStep':newHandStep,'loginRewardStats':loginRewardStats,'leveupRewardStats':leveupRewardStats,'lastGetEmailTime':lastGetEmailTime,'bugTimes':bugTimes,'addEnergyTime':addEnergyTime,'serverTime':serverTime}        self.newHandStep = newHandStep        self.loginRewardStats = loginRewardStats        self.leveupRewardStats = leveupRewardStats        self.lastGetEmailTime = lastGetEmailTime        self.bugTimes = bugTimes        self.addEnergyTime = addEnergyTime        self.serverTime = serverTime        self.towerHightst = 0       #试练塔最大层数        self.vipRewardState = False     #vip奖励领取状态    def getUserOtherDataForDic(self):        return {'newHandStep':self.newHandStep,'loginRewardStats':self.loginRewardStats,'leveupRewardStats':self.leveupRewardStats,'lastGetEmailTime':self.lastGetEmailTime,'bugTimes':self.bugTimes,'addEnergyTime':self.addEnergyTime,'serverTime':self.serverTime}    def getUserOtherDataForProtobuff(self,tmp):        #tmp = Cmds_pb2.UserOtherData()        tmp.newHandStep = self.newHandStep        tmp.loginRewardStats = self.loginRewardStats        tmp.leveupRewardStats = self.leveupRewardStats        tmp.lastGetEmailTime = self.lastGetEmailTime        tmp.bugTimes = self.bugTimes        tmp.addEnergyTime = self.addEnergyTime        tmp.serverTime = int(time.time())        tmp.towerHightstLevel = self.towerHightst        tmp.vipRewardState = self.vipRewardState        return tmp    def getUserOtherDataForProtobuffString(self):        return self.getUserOtherDataForProtobuff().SerializePartialToString()        